$(document).ready(function()
{
  $("#cont").load("../src/calendrier_vue.php?mois="+mois+"&anne="+anne,function(){}); // chargement initiale
  // Déclenchement du code quand on clique sur la flèche gauche
  $("* > #pre").click(function()
  {
    mois--; // décrémenté la variable mois
    if(mois < 1) // Si mois est inférieur a 1
    {
      anne--; // décrémenté année
      mois = 12; // affectation de la valeur 12 à mois
    }
    // Chargement du calendrier
    $("#cont").load("../src/calendrier_vue.php?mois="+mois+"&anne="+anne,function(){});
  });

  // Déclenchement du code quand on clique sur la flèche droite
  $("* > #post").click(function()
  {
    mois++; // Incrémentation de mois
    if(mois > 12) // Si mois est supérieur a 12
    {
      anne++; // Incrémentation année
      mois = 1; // affectation de la valeur 1 à mois
    }
    // Chargement du calendrier
    $("#cont").load("../src/calendrier_vue.php?mois="+mois+"&anne="+anne,function(){});
  });
});
